
public class Ders5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		
		a=a+1;// ==  a+=1
		
		System.out.println("a nın değeri "+a);
		
		int b=10;
		System.out.println("b nin değeri "+b++);
		
		System.out.println("b nin değeri "+b);
		
		int c=10;
		--c;
		System.out.println("c nin değeri "+c);
		
		
		
		
		
	}

}
